import R from 'ramda';
import { FETCH_PHONES_RESOLVE } from 'action-types';

const defState = {
    ids: []
};

export default (state = defState, { type, payload }) => {
    switch(type) {
        case FETCH_PHONES_RESOLVE:
            return R.merge(state, {
                ids: R.pluck('id', payload)
            });
        default:
            return state;
    }
}