import R from 'ramda';

import {
    FETCH_PHONES_RESOLVE
} from 'action-types';

const defState = {};

export default (state = defState, { type, payload }) => {
    switch (type) {
        case FETCH_PHONES_RESOLVE:
            const newValues = R.indexBy(R.prop('id'), payload);
            return R.merge(state, newValues);
        default: return state;
    }
}